import { useDispatch } from 'react-redux'
import { delUser } from '../../store/usersReducer.ts'
import Button from '../UI/Button/Button'
import './UserItem.css'

export default function UserItem({id, name, email}) {
    const dispatch = useDispatch()

    return (
        <li onClick={() => dispatch(delUser(id))} className="list-item">
            <div className="wrapper">
                <h2 className="list-item__title">{name}</h2>
                <span className="list-item__span">{email}</span>
            </div>
            <Button></Button>
        </li>
    )
}
