import Input from "../Input/Input";
import Button from "../Button/Button";
import './Modal.css'
import { toggleState } from "../../../services/toggleState";
import { addUser } from "../../../store/actions";
import { useState } from "react";
import { useDispatch } from "react-redux";

const Modal = ({ modal, setModal, changeStateOfModal, action, id = Date.now() }) => {
    const [email, setEmail] = useState('')
    const [name, setName] = useState('')
    const dispatch = useDispatch()

    const handleSubmit = (e) => {
        e.preventDefault()

        if (name || email) {
            dispatch(addUser({ id, name, email }))
            changeStateOfModal(addUser())
            toggleState(setModal, modal)
        }


    }

    return (
        <form className="form">
            <Input onChange={e => setName(e.target.value)} type="text" placeholder={'Name'} />
            <Input onChange={e => setEmail(e.target.value)} type="email" placeholder={'Email'} />
            <Button onClick={(e) => handleSubmit(e)} style={{ background: '#ffffff', color: '#333' }}>Add</Button>
        </form>
    )
}

export default Modal;
