import { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { toggleState } from '../../services/toggleState'
import { delUser, changeUser } from '../../store/actions'
import Button from '../UI/Button/Button'
import Modal from "../UI/Modal/Modal"
import './UserItem.css'

export default function UserItem({ id, name, email, changeStateOfModal }) {
    const dispatch = useDispatch()
    const [modal, setModal] = useState(false)
    const users = useSelector(state => state.users.users)


    const setUsers = () => {
        users.find(item => item.id = id).name = 'asdf'
        toggleState(setModal, modal)
    }

    const changeList = (e) => {
        console.log(e);
    }

    return (
        <>
            {modal
                ? <Modal modal={modal} setModal={setModal} changeStateOfModal={changeStateOfModal} action={changeUser}/>
                : <></>
            }
            < li className = "list-item" >
                <div className="list-item-wrapper">
                    <h2 className="list-item__title">{name}</h2>
                    <span className="list-item__span">{email}</span>
                </div>
                <div className="button-wrapper">
                    <Button onClick={(e) => changeList(e)}>Change</Button>
                    <Button onClick={() => dispatch(delUser(id))}>Del</Button>
                </div>
            </li >
        </>
    )
}
